import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import { MathComponent } from "mathjax-react";

function QuizPage() {
  const location = useLocation();
  // Quiz information was passed into "state" from Homepage
  const { quiz, topic, difficulty } = location.state;

  // Getting quiz information
  const questions = quiz.qns;

  // Handlers

  const [selectedOptions, setSelectedOptions] = useState({});

  const handleOptionChange = (event, questionIndex) => {
    // Updates new state based on previous state by passing in function
    setSelectedOptions((prevSelectedOptions) => ({
      ...prevSelectedOptions,
      [questionIndex]: parseInt(event.target.value), // event.target.value = option chosen
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle the submission logic here, such as checking the answers and showing the result
  };

  return (
    <div>
      <h1>
        {topic} (Level {difficulty})
      </h1>
      <form onSubmit={handleSubmit}>
        {questions.map((question, index) => (
          <div key={index}>
            {quiz.statement}
            <MathComponent
              tex={question.qn}
              display={false}
              style={{ marginBottom: "2rem" }}
            />
            {question.options.map((option, optionIndex) => (
              <div key={optionIndex}>
                <input
                  type="radio"
                  id={`option_${index}_${optionIndex}`}
                  name={`quizOption_${index}`}
                  value={optionIndex}
                  checked={selectedOptions[index] === optionIndex}
                  onChange={(event) => {
                    console.log("index:");
                    console.log(typeof index);
                    console.log("optionIndex:");
                    console.log(typeof optionIndex);
                    console.log("selectedOptions[index]:");
                    console.log(typeof selectedOptions[index]);
                    handleOptionChange(event, index);
                  }}
                />
                <label htmlFor={`option_${index}_${optionIndex}`}>
                  <MathComponent tex={option} display={false} />
                </label>
              </div>
            ))}
          </div>
        ))}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default QuizPage;
