import React from "react";
import Box from "@mui/material/Box";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";

// Arguments: title, array of string to select from
// Selection will be stored in "value" field of Select
function BasicSelect({ title, elements }) {
  const [selection, setSelection] = React.useState("");

  const handleChange = (event) => {
    setSelection(event.target.value);
  };

  return (
    <Box sx={{ minWidth: 120 }}>
      <InputLabel id="simple-select-label">{title}</InputLabel>
      <Select
        labelId="simple-select-label"
        id="simple-select"
        value={selection} // The item we selected
        label={title}
        onChange={handleChange}
      >
        {elements.map((item) => (
          <MenuItem value={item}>{item}</MenuItem>
        ))}
      </Select>
    </Box>
  );
}

export default BasicSelect;
