import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import {
  Button,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  Container,
  Grid,
  AppBar,
  Toolbar,
  Typography,
} from "@mui/material";
import Quiz from "../components/Quiz";

function HomePage() {
  const [topic, setTopic] = useState("");
  const [difficulty, setDifficulty] = useState("");

  // Effect Hook:
  // Runs the function on the first render
  // And any time any dependency value changes
  useEffect(() => {
    getQuiz();
  }, [topic, difficulty]);

  let getQuiz = async () => {
    if (topic === "" || difficulty === "") return;

    let response = await fetch("/api/generateQuiz?param=haha");
    let data = await response.json();
    console.log(data);
  };

  const handleTopicChange = (event) => {
    setTopic(event.target.value);
  };

  const handleDifficultyChange = (event) => {
    setDifficulty(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault(); // Neccessary?
    // Send form data to backend here

    console.log({ topic, difficulty });
  };

  const topics = ["Expand", "Factorise"];
  const difficulties = ["1", "2", "3"];

  return (
    <Container maxWidth="md">
      <AppBar
        position="static"
        style={{ marginTop: "2rem", marginBottom: "2rem" }}
      >
        <Toolbar>
          <Typography variant="h6">Quizzicalc</Typography>
        </Toolbar>
      </AppBar>
      <form onSubmit={handleSubmit}>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={12} sm={6}>
            <InputLabel id="topic-label">Topic</InputLabel>
            <Select
              labelId="topic-label"
              id="topic"
              value={topic}
              onChange={handleTopicChange}
              fullWidth
            >
              {topics.map((item) => (
                <MenuItem key={item} value={item}>
                  {item}
                </MenuItem>
              ))}
            </Select>
          </Grid>
          <Grid item xs={12} sm={6}>
            <InputLabel id="difficulty-label">Difficulty</InputLabel>
            <Select
              labelId="difficulty-label"
              id="difficulty"
              value={difficulty}
              onChange={handleDifficultyChange}
              fullWidth
            >
              {difficulties.map((item) => (
                <MenuItem key={item} value={item}>
                  {item}
                </MenuItem>
              ))}
            </Select>
          </Grid>
          <Grid item xs={12} container justifyContent="flex-end">
            <Button variant="contained" color="primary" type="submit">
              Submit
            </Button>
          </Grid>
        </Grid>
      </form>

      <Quiz />
    </Container>
  );
}

export default HomePage;
